  // Generiere themenspezifische Titel basierend auf Topic
  if (topic) {
    if (i === 1) {
      title = `Kapitel 1: Einführung in ${topic}`;
      description = `Einführung in das Thema ${topic}, Problemstellung, Relevanz für die Zielgruppe`;
    } else if (i === 2) {
      title = `Kapitel 2: Grundlagen von ${topic}`;
      description = `Grundlegende Konzepte und Definitionen zu ${topic}, theoretischer Hintergrund`;
    } else if (i === chapterCount) {
      title = `Kapitel ${i}: Fazit zu ${topic}`;
      description = `Zusammenfassung der wichtigsten Punkte zu ${topic}, Handlungsempfehlungen, Ausblick`;
    } else {
      title = `Kapitel ${i}: ${topic} - Praktische Anwendung`;
      description = `Praktische Ansätze und Methoden zu ${topic}, Schritt-für-Schritt-Anleitungen`;
    }
  } else {
    // Fallback nur wenn kein Topic vorhanden
    if (i === 1) {
      title = 'Kapitel 1: Einleitung';
      description = 'Einführung in das Thema';
    } else if (i === 2) {
      title = 'Kapitel 2: Grundlagen';
      description = 'Grundlegende Konzepte und Definitionen';
    } else if (i === chapterCount) {
      title = `Kapitel ${i}: Fazit`;
      description = 'Zusammenfassung der wichtigsten Punkte';
    } else {
      title = `Kapitel ${i}: Praktische Anwendung`;
      description = 'Praktische Ansätze und Methoden';
    }
  }

  chapters.push({
    chapterNumber: i,
    title: title,
    targetWords: wordsPerChapter,
    description: description
  });
}

