// Combine & Save Full E-Book
// This node combines all chapters and prepares the full ebook data
// Also prepares checkpoint data for Supabase Node
// FIXED: Sicherstellt, dass immer ein Objekt zurückgegeben wird (nie primitiver Wert)

try {
  // Get input data - handle all edge cases safely
  let json = {};
  let binary = {};

  // Safely extract input data
  if ($input && typeof $input === 'object') {
    if ($input.json && typeof $input.json === 'object') {
      json = $input.json;
    } else if (Array.isArray($input) && $input.length > 0) {
      json = $input[0].json || {};
      binary = $input[0].binary || {};
    } else if (typeof $input.item === 'object' && $input.item.json) {
      json = $input.item.json || {};
      binary = $input.item.binary || {};
    }
  }

  // Ensure json is an object, never null or undefined
  if (!json || typeof json !== 'object' || Array.isArray(json)) {
    json = {};
  }

  // Ensure binary is an object if present
  if (binary && typeof binary !== 'object') {
    binary = {};
  }

  // Extract chapter data and other necessary fields
  let chapters = json.chapters || json.chapterArray || json.collectedChapters || [];
  if (!Array.isArray(chapters)) {
    chapters = [];
  }

  const fullText = json.fullText || json.combinedText || json.text || '';

  // Extract proposal/ebook metadata
  const proposalId = json.proposalId || json.id || json.proposal_id || null;
  const isbn = json.isbn || json.ISBN || null;
  const title = json.title || json.topic || json.thema || 'E-Book';
  const topic = json.topic || json.thema || title || 'E-Book';
  const volumeNumber = json.volume_number || json.volumeNumber || json.volume || 1;

  // Calculate chapter count safely
  let chapterCount = 0;
  if (Array.isArray(chapters)) {
    chapterCount = chapters.length;
  } else if (json.chapterCount && typeof json.chapterCount === 'number') {
    chapterCount = json.chapterCount;
  }

  // Combine all chapters into full text if not already combined
  let combinedText = fullText || '';
  if (!combinedText && Array.isArray(chapters) && chapters.length > 0) {
    combinedText = chapters
      .map(function(ch, idx) {
        if (!ch || typeof ch !== 'object') {
          return '';
        }
        const chapterText = ch.chapterText || ch.text || ch.content || '';
        const chapterTitle = ch.chapterTitle || ch.title || 'Kapitel ' + (idx + 1);
        return '# ' + chapterTitle + '\n\n' + (chapterText || '');
      })
      .filter(function(text) { return text.length > 0; })
      .join('\n\n---\n\n');
  }

  // Prepare output data object - ALWAYS an object
  const output = {};

  // Copy all original data safely
  for (const key in json) {
    if (json.hasOwnProperty(key)) {
      try {
        output[key] = json[key];
      } catch (e) {
        // Skip problematic keys
        console.log('[Combine & Save] Skipped key:', key, e.message);
      }
    }
  }

  // Set combined ebook data
  output.fullText = combinedText || '';
  output.combinedText = combinedText || '';
  output.text = combinedText || '';

  // Set chapter information
  output.chapters = chapters;
  output.chapterArray = chapters;
  output.collectedChapters = chapters;
  output.chapterCount = chapterCount;

  // Set metadata
  output.proposalId = proposalId;
  output.isbn = isbn;
  output.title = title || 'E-Book';
  output.topic = topic || 'E-Book';
  output.thema = topic || 'E-Book';
  output.volume_number = volumeNumber;
  output.volumeNumber = volumeNumber;

  // Set status flags
  output.isCombined = true;
  output.readyForQualityCheck = true;

  // Set timestamp
  output.combinedAt = new Date().toISOString();

  // Prepare checkpoint data for Supabase Node
  output.checkpointData = {
    fullText: combinedText || '',
    chapters: chapters,
    chapterCount: chapterCount,
    proposalId: proposalId,
    isbn: isbn,
    title: title || 'E-Book',
    topic: topic || 'E-Book',
    volumeNumber: volumeNumber
  };
  output.checkpointType = 'full_ebook';

  // Prepare return object - ALWAYS return an array with objects
  const returnItem = { json: output };

  // Include binary data if present and valid
  if (binary && typeof binary === 'object' && !Array.isArray(binary)) {
    const binaryKeys = Object.keys(binary);
    if (binaryKeys.length > 0) {
      returnItem.binary = binary;
    }
  }

  // FIXED: Always return array with object, never primitive
  return [returnItem];

} catch (error) {
  // Error handling - return error information instead of crashing
  // ALWAYS return an object in an array, never primitive
  const errorOutput = {
    error: true,
    errorMessage: error.message || String(error) || 'Unknown error',
    errorType: error.name || 'UnknownError',
    isCombined: false,
    readyForQualityCheck: false
  };

  // Try to keep original data if available
  try {
    if ($input && typeof $input === 'object') {
      let inputJson = {};
      if ($input.json && typeof $input.json === 'object') {
        inputJson = $input.json;
      } else if (Array.isArray($input) && $input.length > 0 && $input[0].json) {
        inputJson = $input[0].json || {};
      } else if ($input.item && $input.item.json) {
        inputJson = $input.item.json || {};
      }

      // Copy safe properties
      for (const key in inputJson) {
        if (inputJson.hasOwnProperty(key)) {
          try {
            errorOutput[key] = inputJson[key];
          } catch (e) {
            // Skip problematic keys
          }
        }
      }
    }
  } catch (e) {
    // Ignore errors when trying to preserve input data
    console.log('[Combine & Save] Error preserving input data:', e.message);
  }

  // FIXED: Always return array with object, never primitive
  return [{ json: errorOutput }];
}

// Quality Check (Spelling, Plagiarism, SEO)
// Verwendet dynamische Mindest-Wortanzahl aus Chat-Eingaben ODER Marktanalyse
const ebookData = $input.first().json || {};
const totalWordCount = ebookData.totalWordCount || 0;
const fullContent = ebookData.fullContent || '';

// Hole Mindest-Wortanzahl und Mindest-Kapitelanzahl: Priorität Chat > Marktanalyse > Standard
let MIN_WORD_COUNT = 1500; // Fallback
let MIN_CHAPTERS = 3; // Fallback

// Versuche Chat-Eingaben
let chatInputData = {};
try {
  chatInputData = $('Extract Chat Input').item.json || {};
  if (chatInputData.chapterCount && chatInputData.wordsPerChapter) {
    MIN_WORD_COUNT = chatInputData.chapterCount * chatInputData.wordsPerChapter;
    MIN_CHAPTERS = chatInputData.chapterCount;
    console.log('[Quality Check] Using Chat Input parameters');
  }
} catch (e) {
  // Versuche Marktanalyse
  try {
    const marketData = $('Merge Market Analysis Params').item.json || {};
    if (marketData.chapterCount && marketData.wordsPerChapter) {
      MIN_WORD_COUNT = marketData.chapterCount * marketData.wordsPerChapter;
      MIN_CHAPTERS = marketData.chapterCount;
      console.log('[Quality Check] Using Market Analysis parameters');
    }
  } catch (e2) {
    // Fallback: Verwende minWordCount aus ebookData
    MIN_WORD_COUNT = ebookData.min_word_count || ebookData.minWordCount || 1500;
    MIN_CHAPTERS = (ebookData.chapters || []).length || 3;
    console.log('[Quality Check] Using E-Book Data parameters');
  }
}

// Basis-Quality-Checks
const checks = {
  wordCount: {
    passed: totalWordCount >= MIN_WORD_COUNT,
    actual: totalWordCount,
    required: MIN_WORD_COUNT,
    message: totalWordCount >= MIN_WORD_COUNT 
      ? `Wortanzahl OK: ${totalWordCount} Wörter` 
      : `Wortanzahl zu niedrig: ${totalWordCount} < ${MIN_WORD_COUNT}`
  },
  contentLength: {
    passed: fullContent.length > 0,
    actual: fullContent.length,
    required: 1,
    message: fullContent.length > 0 ? 'Inhalt vorhanden' : 'Kein Inhalt'
  },
  chapters: {
    passed: (ebookData.chapters || []).length >= MIN_CHAPTERS,
    actual: (ebookData.chapters || []).length,
    required: MIN_CHAPTERS,
    message: (ebookData.chapters || []).length >= MIN_CHAPTERS 
      ? `Kapitel OK: ${(ebookData.chapters || []).length} Kapitel` 
      : `Zu wenige Kapitel: ${(ebookData.chapters || []).length} < ${MIN_CHAPTERS}`
  }
};

const allPassed = Object.values(checks).every(check => check.passed);

return [{
  json: {
    ...ebookData,
    quality_check: allPassed ? 'PASSED' : 'REJECTED',
    quality_checks: checks,
    overall: {
      status: allPassed ? 'success' : 'error',
      message: allPassed ? 'Alle Quality Checks bestanden' : 'Quality Checks fehlgeschlagen'
    }
  }
}];

// Konvertiere Cover-Bild von Bild Baustein zu Binary
// FIXED: Generate Cover gibt Daten im dritten Output-Array zurück (output[2])
// WICHTIG: Prüfe alle Input-Items und Output-Arrays
const allInputs = $input.all();

console.log('[Convert Cover] ===== CONVERT COVER TO BINARY START =====');
console.log('[Convert Cover] Input items count:', allInputs.length);

// WICHTIG: Generate Cover gibt Daten in verschiedenen Output-Arrays zurück
// Prüfe alle Input-Items systematisch
let bildBausteinOutput = null;
let inputData = {};

// Durchsuche alle Input-Items
for (let i = 0; i < allInputs.length; i++) {
  const item = allInputs[i];
  const json = item.json || {};
  const binary = item.binary || {};

  console.log(`[Convert Cover] Input item ${i}:`);
  console.log(`  JSON keys:`, Object.keys(json));
  console.log(`  Binary keys:`, Object.keys(binary));
  console.log(`  Has bestImage:`, !!json.bestImage);
  console.log(`  Has allImages:`, !!json.allImages);
  console.log(`  Has imageUrl:`, !!json.imageUrl);
  console.log(`  Status:`, json.status);

  // Prüfe ob dieses Item Bild-Daten enthält
  if (json.bestImage && Object.keys(json.bestImage).length > 0) {
    bildBausteinOutput = json;
    console.log(`[Convert Cover] ✅ Bild-Daten in Input item ${i} gefunden (bestImage)`);
    break;
  }

  if (json.allImages && Array.isArray(json.allImages) && json.allImages.length > 0) {
    bildBausteinOutput = json;
    console.log(`[Convert Cover] ✅ Bild-Daten in Input item ${i} gefunden (allImages)`);
