    console.log(`[Convert Cover] ✅ Bild-Daten in Input item ${i} gefunden (imageUrl)`);
    break;
  }

  // Prüfe Binary-Daten
  if (binary.data && binary.data.data) {
    bildBausteinOutput = { ...json, _hasBinary: true, binary: binary };
    console.log(`[Convert Cover] ✅ Binary-Daten in Input item ${i} gefunden`);
    break;
  }

  // Fallback: Verwende erstes Item mit Daten
  if (Object.keys(json).length > 0 && !inputData.topic) {
    inputData = json;
  }
}

// Wenn keine Bild-Daten in Input gefunden, versuche von Generate Cover Node zu holen
if (!bildBausteinOutput || (!bildBausteinOutput.bestImage && !bildBausteinOutput.allImages && !bildBausteinOutput.imageUrl && !bildBausteinOutput._hasBinary)) {
  console.log('[Convert Cover] ⚠️ Keine Bild-Daten in Input gefunden, versuche von Generate Cover Node');

