console.log('[Convert Cover] Bild Baustein Output keys:', Object.keys(bildBausteinOutput));
console.log('[Convert Cover] bestImage:', bildBausteinOutput.bestImage);
console.log('[Convert Cover] allImages:', bildBausteinOutput.allImages);
console.log('[Convert Cover] imageUrl:', bildBausteinOutput.imageUrl);
console.log('[Convert Cover] status:', bildBausteinOutput.status);
console.log('[Convert Cover] Has Binary:', bildBausteinOutput._hasBinary);

// Prüfe zuerst Binary-Daten (höchste Priorität)
if (bildBausteinOutput._hasBinary && bildBausteinOutput.binary && bildBausteinOutput.binary.data) {
  const binaryData = bildBausteinOutput.binary.data;
  const base64Data = binaryData.data || '';
