  try {
    // Falls bereits geparste Trends vorhanden
    if (data.trends && Array.isArray(data.trends)) {
      return {
        trends: data.trends,
        market_insights: data.marketInsights || data.market_insights || ''
      };
    }

    // Falls noch in Response-Format
    if (data.choices?.[0]?.message?.content) {
      const content = data.choices[0].message.content;
      const jsonMatch = content.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        return JSON.parse(jsonMatch[0]);
      }
    }
  } catch (e) {
    console.error('[Market Analysis] Parse error:', e);
  }
  return { trends: [], market_insights: '' };
}

