// Generiere ISBN-13 Nummer
// FIXED: Verwendet $input.item.json statt $input.first().json für Run Once for Each Item Modus
const inputData = $input.item.json || {};

// Hole Topic für ISBN-Generierung
const topic = inputData.topic || inputData.thema || 'ebook';
const volumeNumber = inputData.volume_number || inputData.volumeNumber || 1;

// Generiere eine eindeutige ISBN-13 basierend auf Topic und Volume
// ISBN-13 Format: 978-3-XXXXX-XXX-X (978 = E-Book-Präfix, 3 = Deutschland)

// Erstelle einen Hash aus Topic und Volume für Konsistenz
function simpleHash(str) {
  let hash = 0;
  for (let i = 0; i < str.length; i++) {
    const char = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32bit integer
  }
  return Math.abs(hash);
}

const hash = simpleHash(topic + volumeNumber.toString());

// Generiere 9-stellige Nummer aus Hash
const baseNumber = (hash % 999999999).toString().padStart(9, '0');

// ISBN-13 Präfix: 978-3 (E-Book, Deutschland)
const prefix = '9783';
const isbnWithoutCheck = prefix + baseNumber;

// Berechne Prüfziffer (ISBN-13)
let sum = 0;
for (let i = 0; i < 12; i++) {
  const digit = parseInt(isbnWithoutCheck[i]);
  sum += digit * (i % 2 === 0 ? 1 : 3);
}
const checkDigit = (10 - (sum % 10)) % 10;
const isbn13 = isbnWithoutCheck + checkDigit.toString();

// Formatiere ISBN mit Bindestrichen: 978-3-XXXXX-XXX-X
const formattedISBN = `${isbn13.substring(0, 3)}-${isbn13.substring(3, 4)}-${isbn13.substring(4, 9)}-${isbn13.substring(9, 12)}-${isbn13.substring(12)}`;

// Bereinige ISBN (nur Ziffern) für Dateinamen
const isbnClean = isbn13;

console.log('[Generate ISBN] Topic:', topic);
console.log('[Generate ISBN] Volume:', volumeNumber);
console.log('[Generate ISBN] ISBN-13:', formattedISBN);
console.log('[Generate ISBN] ISBN Clean:', isbnClean);

// Kombiniere Input-Daten mit ISBN
return {
  json: {
    ...inputData,
    isbn: formattedISBN,
    isbn13: isbn13,
    isbnClean: isbnClean,
    isbnGenerated: true
  }
};

